import React, { useState, useEffect } from 'react';
import './Signup.css';

const Signup = ({ eventId }) => {
  const [name, setName] = useState('');
  const [attendees, setAttendees] = useState([]); // Ensure this is an array, not undefined
  const [rsvpStatus, setRsvpStatus] = useState('Attending');

  // Fetch attendees from backend when the component mounts
  useEffect(() => {
    const fetchAttendees = async () => {
      try {
        const response = await fetch(`http://localhost:5000/events/${eventId}/attendees`);
        const data = await response.json();
        setAttendees(data.attendees || []); // Ensure we set it to an array
      } catch (error) {
        console.error('Error fetching attendees:', error);
      }
    };

    fetchAttendees();
  }, [eventId]);

  const handleSignup = async () => {
    if (!eventId) {
      console.error('Event ID is required');
      return;
    }
  
    try {
      await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          attendee: { name, rsvpStatus },
        }),
      });
      // Handle success
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };
  

  const toggleRsvpStatus = () => {
    setRsvpStatus((prevStatus) => (prevStatus === 'Attending' ? 'Not Attending' : 'Attending'));
  };

  return (
    <div className="signup-container">
      <h2 className="signup-heading">Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="signup-input"
      />
      <div className="signup-buttons-container">
        <button onClick={toggleRsvpStatus} className="signup-toggle-button">
          RSVP {rsvpStatus === 'Attending' ? 'No' : 'Yes'}
        </button>
        <button onClick={handleSignup} className="signup-confirm-button">
          Confirm RSVP
        </button>
      </div>
      <h3 className="signup-attendees-heading">Attendees List</h3>
      <ul className="signup-attendees-list">
        {attendees.map((attendee, index) => (
          <li key={index} className="signup-attendee-item">
            {attendee.name} - {attendee.rsvpStatus}
          </li>
        ))}
      </ul>
      <p className="signup-total-attendees">Total Attendees: {attendees.length}</p>
    </div>
  );
};

export default Signup;
