import React, { useState, useEffect, useRef } from 'react';
import './EventList.css'; // Import CSS file for styles
import EventDetail from './EventDetail';

// Dummy data for now
// const fetchEvents = async () => {
//   // In a real application, this would be an API call
//   return [
//     { id: '1', name: 'Event One', date: '2024-09-20', time: '18:00', location: 'Venue A', description: 'Description of Event One' },
//     { id: '2', name: 'Event Two', date: '2024-09-25', time: '19:00', location: 'Venue B', description: 'Description of Event Two' },
//   ];
// };

const fetchEvents = async () => {
    try {
      const response = await fetch('http://localhost:5000/events');
      const data = await response.json();
      return data;
    } catch (error) {
      console.error('Error fetching events:', error);
      return [];
    }
  };
  

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [fadeAnim, setFadeAnim] = useState(0);

  useEffect(() => {
    const loadEvents = async () => {
      const eventsData = await fetchEvents();
      setEvents(eventsData);
    };

    loadEvents();
  }, []);

  useEffect(() => {
    let start = 0;
    const fade = () => {
      start += 0.05;
      setFadeAnim(start);
      if (start < 1) {
        requestAnimationFrame(fade);
      }
    };
    fade();
  }, [events]);

  if (selectedEvent) {
    return <EventDetail event={selectedEvent} goBack={() => setSelectedEvent(null)} />;
  }

  return (
    <div className="container" style={{ opacity: fadeAnim }}>
      <h1 className="header">Upcoming Events</h1>
      <div className="event-list">
        {events.map((item) => (
          <div key={item.id} className="event" onClick={() => setSelectedEvent(item)}>
            <h2 className="event-name">{item.name}</h2>
            <p className="event-details">{item.date} at {item.time}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventList;
