import React, { useState } from 'react';
import './Signup.css'; // Import CSS file for styles

const Signup = ({ eventId, onSuccess }) => {
  const [name, setName] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!name) {
      setError('Name is required');
      return;
    }

    try {
      await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          attendee: { name, rsvpStatus: 'Attending' }, // Default RSVP status
        }),
      });
      onSuccess(); // Callback to refresh the attendees list
    } catch (error) {
      console.error('Error signing up:', error);
      setError('Failed to sign up');
    }
  };

  return (
    <div className="signup-container">
      <h1 className="signup-title">Sign Up for Event</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Your Name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className="signup-input"
        />
        {error && <p className="signup-error">{error}</p>}
        <button type="submit" className="signup-button">Sign Up</button>
      </form>
    </div>
  );
};

export default Signup;
