import React, { useState, useEffect } from 'react';
import './Signup.css'; // Import your CSS file

const Signup = () => {
  const [name, setName] = useState('');
  const [rsvpStatus, setRsvpStatus] = useState('Attending');
  const [eventId, setEventId] = useState(null); // State to store the event ID
  const [events, setEvents] = useState([]); // State to store events
  const [attendees, setAttendees] = useState([]);

  // Fetch events from the server
  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const response = await fetch('http://localhost:5000/events');
        const data = await response.json();
        setEvents(data);
        // Optionally select an event ID to work with
        if (data.length > 0) {
          setEventId(data[0]._id); // Example: Select the first event's ID
        }
      } catch (error) {
        console.error('Error fetching events:', error);
      }
    };

    fetchEvents();
  }, []);

  const handleSignup = async () => {
    if (!eventId) {
      console.error('Event ID is required');
      return;
    }

    try {
      const response = await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          name,
          rsvpStatus
        }),
      });

      if (response.ok) {
        // Handle success
        console.log('RSVP submitted successfully');
      } else {
        // Handle server errors
        console.error('Failed to submit RSVP');
      }
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  return (
    <div className="signup-container">
      <h2 className="signup-heading">Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="signup-input"
      />
      <div className="signup-buttons-container">
        <button onClick={handleSignup} className="signup-confirm-button">
          Confirm RSVP
        </button>
      </div>
      <h3 className="signup-events-heading">Available Events</h3>
      <ul className="signup-events-list">
        {events.map((event) => (
          <li key={event._id} onClick={() => setEventId(event._id)} className="signup-event-item">
            {event.name}
          </li>
        ))}
      </ul>
      <h3 className="signup-attendees-heading">Attendees List</h3>
      <ul className="signup-attendees-list">
        {attendees.map((attendee) => (
          <li key={attendee._id} className="signup-attendee-item">
            {attendee.name} - {attendee.rsvpStatus}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Signup;
