import React, { useState, useEffect, useRef } from 'react';
import './EventDetail.css'; // Import CSS file for styles
import Signup from '../Signup'; // Import Signup component

const EventDetail = ({ event, goBack }) => {
  const [rsvpStatus, setRsvpStatus] = useState('Not Responded');
  const [attendees, setAttendees] = useState([]);
  const [showSignup, setShowSignup] = useState(false);
  const fadeAnim = useRef(0);

  useEffect(() => {
    let start = 0;
    const fade = () => {
      start += 0.05;
      fadeAnim.current = start;
      document.getElementById('fade').style.opacity = start;
      if (start < 1) {
        requestAnimationFrame(fade);
      }
    };
    fade();
  }, []);

  const handleRSVP = async (status) => {
    setRsvpStatus(status);

    try {
      await fetch(`http://localhost:5000/events/${event.id}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          attendee: { name: 'User Name', rsvpStatus: status },
        }),
      });

      fetchAttendees();
    } catch (error) {
      console.error('Error updating RSVP:', error);
    }
  };

  const fetchAttendees = async () => {
    try {
      const response = await fetch(`http://localhost:5000/events/${event.id}/attendees`);
      const data = await response.json();
      setAttendees(data);
    } catch (error) {
      console.error('Error fetching attendees:', error);
    }
  };

  useEffect(() => {
    fetchAttendees();
  }, [event]);

  return (
    <div id="fade" className="container">
      <button className="back-button" onClick={goBack}>Back</button>
      <h1 className="title">{event.name}</h1>
      <p className="detail">Date: {event.date}</p>
      <p className="detail">Time: {event.time}</p>
      <p className="detail">Location: {event.location}</p>
      <p className="detail">Description: {event.description}</p>
      <p className="rsvp-status">RSVP Status: {rsvpStatus}</p>
      <div className="button-container">
        <button className="button" onClick={() => handleRSVP('Attending')}>
          RSVP Yes
        </button>
        <button className="button" onClick={() => handleRSVP('Not Attending')}>
          RSVP No
        </button>
        <button className="button" onClick={() => setShowSignup(true)}>
          Sign Up
        </button>
      </div>
      <div className="attendees-container">
        <h2 className="attendees-header">Attendees:</h2>
        {attendees.length > 0 ? (
          attendees.map((attendee, index) => (
            <p key={index} className="attendee">
              {attendee.name} - {attendee.rsvpStatus}
            </p>
          ))
        ) : (
          <p>No attendees yet.</p>
        )}
      </div>
      {showSignup && <Signup eventId={event.id} onSuccess={() => { setShowSignup(false); fetchAttendees(); }} />}
    </div>
  );
};

export default EventDetail;
