// src/components/RSVPButton.js
import React from 'react';
import axios from 'axios';

const RSVPButton = ({ eventId }) => {
  const handleRSVP = async () => {
    const userId = "someUserId"; // Fetch from user context or auth
    await axios.post(`/api/events/${eventId}/rsvp`, { userId });
    alert("You have successfully RSVP'd!");
  };

  return <button onClick={handleRSVP}>RSVP</button>;
};

export default RSVPButton;
