// export default Signup;

import React, { useState } from 'react';

const Signup = () => {
  const [name, setName] = useState('');
  const [attendees, setAttendees] = useState([]);
  const [rsvpStatus, setRsvpStatus] = useState('Attending'); // Default status

  const handleSignup = () => {
    if (name.trim() === '') {
      console.error('Name is required');
      return;
    }

    // Create new attendee object
    const newAttendee = { name, rsvpStatus };
    
    // Update the list of attendees
    setAttendees((prevAttendees) => [...prevAttendees, newAttendee]);

    // Clear the input field and reset RSVP status
    setName('');
    setRsvpStatus('Attending'); // Reset to default status
  };

  const toggleRsvpStatus = () => {
    setRsvpStatus((prevStatus) => (prevStatus === 'Attending' ? 'Not Attending' : 'Attending'));
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <button onClick={toggleRsvpStatus}>
        RSVP {rsvpStatus === 'Attending' ? 'No' : 'Yes'}
      </button>
      <button onClick={handleSignup}>Confirm RSVP</button>
      <h3>Attendees List</h3>
      <ul>
        {attendees.map((attendee, index) => (
          <li key={index}>{attendee.name} - {attendee.rsvpStatus}</li>
        ))}
      </ul>
      <p>Total Attendees: {attendees.length}</p>
    </div>
  );
};
const styles = {
  container: {
    maxWidth: '600px',
    margin: '0 auto',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 4px 8px rgba(0,0,0,0.1)',
    backgroundColor: '#f9f9f9',
  },
  heading: {
    textAlign: 'center',
    color: '#333',
  },
  input: {
    width: 'calc(100% - 22px)',
    padding: '10px',
    borderRadius: '4px',
    border: '1px solid #ddd',
    marginBottom: '10px',
    display: 'block',
    margin: '0 auto',
  },
  buttonsContainer: {
    textAlign: 'center',
  },
  toggleButton: {
    backgroundColor: '#007bff',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '4px',
    cursor: 'pointer',
    marginRight: '10px',
    transition: 'background-color 0.3s',
  },
  confirmButton: {
    backgroundColor: '#28a745',
    color: '#fff',
    border: 'none',
    padding: '10px 20px',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background-color 0.3s',
  },
  toggleButtonHover: {
    backgroundColor: '#0056b3',
  },
  confirmButtonHover: {
    backgroundColor: '#218838',
  },
  attendeesHeading: {
    marginTop: '20px',
    color: '#333',
  },
  attendeesList: {
    listStyleType: 'none',
    padding: '0',
    margin: '0',
  },
  attendeeItem: {
    padding: '10px',
    borderBottom: '1px solid #ddd',
  },
  totalAttendees: {
    textAlign: 'center',
    marginTop: '10px',
    color: '#666',
  },
};

export default Signup;
