import React, { useState, useEffect, useRef } from 'react';
import { useHistory } from 'react-router-dom';
import { View, Text, Button, StyleSheet, FlatList, TouchableOpacity, Animated } from 'react-native';

const fetchEvents = async () => {
  const response = await fetch('/api/events');
  const data = await response.json();
  return data;
};

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const history = useHistory();

  useEffect(() => {
    const loadEvents = async () => {
      const eventsData = await fetchEvents();
      setEvents(eventsData);
    };

    loadEvents();
  }, []);

  useEffect(() => {
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 500,
      useNativeDriver: true,
    }).start();
  }, [events]);

  if (selectedEvent) {
    return <EventDetail event={selectedEvent} goBack={() => setSelectedEvent(null)} />;
  }

  return (
    <Animated.div style={{ ...styles.container, opacity: fadeAnim }}>
      <Text style={styles.header}>Upcoming Events</Text>
      <div style={styles.eventList}>
        {events.map((event) => (
          <TouchableOpacity
            key={event.id}
            style={styles.event}
            onClick={() => setSelectedEvent(event)}
          >
            <Text style={styles.eventName}>{event.name}</Text>
            <Text style={styles.eventDetails}>{event.date} at {event.time}</Text>
          </TouchableOpacity>
        ))}
      </div>
    </Animated.div>
  );
};

const styles = StyleSheet.create({
  container: {
    padding: '20px',
    backgroundColor: '#f5f5f5',
  },
  header: {
    fontSize: '24px',
    fontWeight: 'bold',
    marginBottom: '20px',
    color: '#333',
  },
  eventList: {
    display: 'flex',
    flexDirection: 'column',
  },
  event: {
    backgroundColor: '#fff',
    padding: '15px',
    marginBottom: '15px',
    borderRadius: '8px',
    boxShadow: '0px 5px 10px rgba(0, 0, 0, 0.2)',
    cursor: 'pointer',
  },
  eventName: {
    fontSize: '18px',
    fontWeight: 'bold',
    color: '#333',
  },
  eventDetails: {
    fontSize: '14px',
    color: '#666',
  },
});

export default EventList;
