import React, { useState } from 'react';
import './Signup.css'; // Import your CSS file

const Signup = ({ eventId, onSuccess }) => {
  const [name, setName] = useState('');
  const [rsvpStatus, setRsvpStatus] = useState('Attending');

  const handleSignup = async () => {
    if (name.trim() === '') {
      console.error('Name is required');
      return;
    }

    const newAttendee = { name, rsvpStatus };

    try {
      await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          attendee: newAttendee,
        }),
      });

      onSuccess(); // Refresh the attendees list
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  return (
    <div className="signup-container">
      <h2 className="signup-heading">Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="signup-input"
      />
      <div className="signup-buttons-container">
        <button onClick={() => setRsvpStatus('Attending')} className="signup-toggle-button">
          RSVP Yes
        </button>
        <button onClick={() => setRsvpStatus('Not Attending')} className="signup-toggle-button">
          RSVP No
        </button>
        <button onClick={handleSignup} className="signup-confirm-button">
          Confirm RSVP
        </button>
      </div>
      <p className="signup-current-status">Current RSVP Status: {rsvpStatus}</p>
    </div>
  );
};

export default Signup;
