import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Signup.css'; // Import your CSS file

const Signup = ({ eventId }) => {
  const [attendees, setAttendees] = useState([]);
  const [error, setError] = useState(null);

  useEffect(() => {
    const fetchAttendees = async () => {
      try {
        const response = await axios.get(`http://localhost:5000/events/${eventId}/attendees`);
        setAttendees(response.data.attendees);
      } catch (err) {
        setError(err.message);
      }
    };

    fetchAttendees();
  }, [eventId]);

  const handleRSVP = async (status) => {
    try {
      await axios.post(`http://localhost:5000/events/${eventId}/rsvp`, { status });
      // Optionally, you can refresh the attendees list here if needed
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="signup-container">
      <h1>Event Attendees</h1>
      {error && <p className="error">{error}</p>}
      <div className="attendees-list">
        {attendees.map(attendee => (
          <div key={attendee._id} className="attendee-item">
            <p>{attendee.name}</p>
            <button onClick={() => handleRSVP('attending')}>RSVP Attending</button>
            <button onClick={() => handleRSVP('not attending')}>RSVP Not Attending</button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Signup;
