import React, { useState } from 'react';

const Signup = ({ eventId, onSuccess }) => {
  const [name, setName] = useState('');

  const handleSignup = async () => {
    try {
      await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          attendee: { name, rsvpStatus: 'Attending' }, // Assuming default RSVP status as 'Attending'
        }),
      });

      onSuccess(); // Refresh the attendees list
    } catch (error) {
      console.error('Error during signup:', error);
    }
  };

  return (
    <div>
      <h2>Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
      />
      <button onClick={handleSignup}>Confirm RSVP</button>
    </div>
  );
};

export default Signup;
