import React, { useState, useEffect } from 'react';
import './EventList.css'; // Import CSS file for styles
import EventDetail from './EventDetail';

const fetchEvents = async () => {
  try {
    const response = await fetch('http://localhost:5000/events');
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error fetching events:', error);
    return [];
  }
};

// Function to send attendee data to backend
const addAttendee = async (eventId, attendee) => {
  try {
    const response = await fetch(`http://localhost:5000/events/${eventId}/attendees`, {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ attendee }), // Send attendee details as JSON
    });
    if (!response.ok) {
      throw new Error('Error adding attendee');
    }
    const data = await response.json();
    return data;
  } catch (error) {
    console.error('Error adding attendee:', error);
    return null;
  }
};

const EventList = () => {
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState(null);
  const [newAttendee, setNewAttendee] = useState({ name: '', rsvpStatus: '' }); // Track new attendee form data
  const [fadeAnim, setFadeAnim] = useState(0);

  useEffect(() => {
    const loadEvents = async () => {
      const eventsData = await fetchEvents();
      setEvents(eventsData);
    };

    loadEvents();
  }, []);

  useEffect(() => {
    let start = 0;
    const fade = () => {
      start += 0.05;
      setFadeAnim(start);
      if (start < 1) {
        requestAnimationFrame(fade);
      }
    };
    fade();
  }, [events]);

  const handleAddAttendee = async (e) => {
    e.preventDefault(); // Prevent form submission
    if (selectedEvent) {
      const result = await addAttendee(selectedEvent._id, newAttendee);
      if (result) {
        // Update event attendees with the new attendee
        setSelectedEvent({
          ...selectedEvent,
          attendees: result, // Update attendees with response data
        });
      }
    }
  };

  if (selectedEvent) {
    return (
      <div>
        <EventDetail event={selectedEvent} goBack={() => setSelectedEvent(null)} />
        <h2>Add Attendee</h2>
        <form onSubmit={handleAddAttendee}>
          <label>
            Name:
            <input
              type="text"
              value={newAttendee.name}
              onChange={(e) => setNewAttendee({ ...newAttendee, name: e.target.value })}
              required
            />
          </label>
          <label>
            RSVP Status:
            <input
              type="text"
              value={newAttendee.rsvpStatus}
              onChange={(e) => setNewAttendee({ ...newAttendee, rsvpStatus: e.target.value })}
              required
            />
          </label>
          <button type="submit">Add Attendee</button>
        </form>
      </div>
    );
  }

  return (
    <div className="container" style={{ opacity: fadeAnim }}>
      <h1 className="header">Upcoming Events</h1>
      <div className="event-list">
        {events.map((item) => (
          <div key={item._id} className="event" onClick={() => setSelectedEvent(item)}>
            <h2 className="event-name">{item.name}</h2>
            <p className="event-details">
              {item.date} at {item.time}
            </p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default EventList;
