import React, { useState } from 'react';
import './Signup.css'; // Import your CSS file

const Signup = () => {
  const [name, setName] = useState('');
  const [attendees, setAttendees] = useState([]);
  const [rsvpStatus, setRsvpStatus] = useState('Attending');
  const handleRSVP = async (status) => {
    setRsvpStatus(status);
    setShowSignup(true); // Show signup form after RSVP

    try {
      // You could also send the RSVP status here if needed
    } catch (error) {
      console.error('Error updating RSVP:', error);
    }
  };
  const handleSignup = () => {
    if (name.trim() === '') {
      console.error('Name is required');
      return;
    }

    const newAttendee = { name, rsvpStatus };
    setAttendees((prevAttendees) => [...prevAttendees, newAttendee]);
    setName('');
    setRsvpStatus('Attending');
  };

  const toggleRsvpStatus = () => {
    setRsvpStatus((prevStatus) => (prevStatus === 'Attending' ? 'Not Attending' : 'Attending'));
  };

  return (
    <div className="signup-container">
      <h2 className="signup-heading">Sign Up</h2>
      <input
        type="text"
        placeholder="Enter your name"
        value={name}
        onChange={(e) => setName(e.target.value)}
        className="signup-input"
      />
      <div className="signup-buttons-container">
        <button onClick={toggleRsvpStatus} className="signup-toggle-button">
          RSVP {rsvpStatus === 'Attending' ? 'No' : 'Yes'}
        </button>
        <button onClick={handleSignup} className="signup-confirm-button">
          Confirm RSVP
        </button>
      </div>
      <h3 className="signup-attendees-heading">Attendees List</h3>
      <ul className="signup-attendees-list">
        {attendees.map((attendee, index) => (
          <li key={index} className="signup-attendee-item">
            {attendee.name} - {attendee.rsvpStatus}
          </li>
        ))}
      </ul>
      <p className="signup-total-attendees">Total Attendees: {attendees.length}</p>
    </div>
  );
};

export default Signup;
